void yyFlexLexer::goto_initial_state() 
{
  BEGIN INITIAL;
}

void yyFlexLexer::goto_code_copy_mode()
{
  yy_push_state(COPY_CODE);
}

void yyFlexLexer::finish_mode()
{
  yy_pop_state();
}

void yyFlexLexer::set_input_stream( std::istream &in ) 
{
  yyin = &in;
}

